    } else {
        "hello username"
    }

    return (
        <div>
            <Banner
                logo="Social Media 2.0"
                url={ window.location.pathname }
                items={[
                    { "content": "Log In", "url": "/LogIn" },
                    { "content": "Sign Up", "url": "/SignUp" }
                ]} />
            <div>hello</div>
        </div>
    )
}
