import React from "react";
import '../Assets/App.css';
import Body from '../Components/Body.js'

function getCookie(cname) {
    let name = cname + "=";
    let decodedCookie = decodeURIComponent(document.cookie);
    let ca = decodedCookie.split(';');
    for(let i = 0; i < ca.length; i++) {
      let c = ca[i];
      while (c.charAt(0) === ' ') {
        c = c.substring(1);
      }
      if (c.indexOf(name) === 0) {
        return c.substring(name.length, c.length);
      }
    }
    return "";
}

export default function Home() {
    let urlParam = window.location.search;
    let uname = urlParam.split('=')[1];
    let hmm = 0
    let hmm2 = 0
    let hmm3 = 0

    let cookieName = getCookie("username")
    cookieName = cookieName.replace(/[\u{0080}-\u{FFFF}]/gu,"");



    console.log(cookieName.length)
    if(uname !== "") {
        hmm=1
        if(getCookie("username") === "") {
            hmm2=1
            if(getCookie("username") !== "undefined") {
                hmm3=1
                //if((uname !== "") && (getCookie("username") === "") && (getCookie("username") !== "undefined")) {//reloads page once
                document.cookie = "username="+uname;
//                window.location.reload()
            }
        }
    }
    document.cookie = "reloadMe=true";//used in the sign out page
    return (
        <div>
            <div>
                {hmm}
                {hmm2}
                {hmm3}
                <Body/>
            </div>
        </div>
    );
}
